   height: 70,
    fontFamily: "arial",
    color: "black",
    backgroundColor: 'white',
    paddingTop: 15,
    
    
  },
  welcome1:{
    
    width: "100%",
    textAlign: 'center',
    top: 0,
    left: 0,
    position: "absolute",
    fontFamily: "arial",
    color: "#fff",
    
    
  },
  input: {

 width: "70%",
 backgroundColor: "#fff",
 padding: 15,
 marginBottom: 50,
 
 
  },
  userBtn: {
    backgroundColor: '#663D14',
    padding: 15,
    width: "45%",
    
  },
  lblTxt:{
    fontSize: 18,
    textAlign: 'center',
    color: 'white',
    //color: 'black',
    textAlign: 'left',
  }
  ,
  btnTxt: {
    fontSize: 18,
    textAlign: 'right',
    color: 'white',
    paddingRight: 5,
    backgroundColor: 'black',
    width: 80,
    
  },
  btnTxt1: {
